#pragma config(Sensor, S3,		 HTSMUX,				 sensorI2CCustom)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "drivers/hitechnic-sensormux.h"
#include "drivers/lego-light.h"

const tMUXSensor LLIGHT = msensor_S3_3;
const tMUXSensor RLIGHT = msensor_S3_4;

task main() {
	LSsetActive(LLIGHT);
	LSsetActive(RLIGHT);
	wait1Msec(100);
	while(true) {
		short leftLight = LSvalNorm(LLIGHT);
		short rightLight = LSvalNorm(RLIGHT);
		nxtDisplayCenteredTextLine(3, "llight: %d", leftLight);
		nxtDisplayCenteredTextLine(4, "rlight: %d", rightLight);
	}
}
